require_once dirname(__FILE__) . '/../../../libs/Qwin/Bind.php';

/**
 * Test class for Qwin_Bind.
 * Generated by PHPUnit on 2012-01-18 at 09:08:45.
 */
class Qwin_BindTest extends PHPUnit_Framework_TestCase {

    /**
     * @var Qwin_Bind
     */
    protected $object;

    /**
     * Sets up the fixture, for example, opens a network connection.
     * This method is called before a test is executed.
     */
    protected function setUp() {
        $this->object = new Qwin_Bind;
    }

    /**
     * Tears down the fixture, for example, closes a network connection.
     * This method is called after a test is executed.
     */
    protected function tearDown() {

    }

    /**
     * variable for testCall method
     *
     * @var string
     */
    protected $_state;

    /**
     * @covers Qwin_Bind::call
     */
    public function testCall() {
        $widget = $this->object;

        $widget->bind('test', array($this, 'setTextState'));

        $widget->trigger('test', array(
            'state' => 'testing',
        ));

        $this->assertEquals('testing', $this->_state);
    }

    public function setTextState($event, $state)
    {
        $this->_state = $state;
    }
}